package arrary;

public class h {

	public static void main(String[] args) {
		// TODO Auto-generated method st
		int k=1; int l=10; int m=11; int n=20;
		for(int i=1;i<=4;i++) {
			if(i==1) {
				for(int j=1;j<=5;j++) 
					{
						System.out.print(j);
				
						}}
			for(int j=1;j<=5;j++) {
				if(i==2) {
					System.out.print(l--);
				}}
			for(int j=1;j<=5;j++) {
				if(i==3) {
					System.out.print(m++);
				}}
			for(int j=1;j<=5;j++) {
				if(i==4) {
					System.out.print(n--);
				}}
			System.out.println();
			}
			}
	}

	


