package arrary;

public class pattren2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int gh=1;
		char ch='a';
		for(int i=1;i<=4;i++) {
			if(i==1||i==2) {
				for(int j=1;j<=4;j++) {
					System.out.print(gh++);
				}				
			System.out.println();
			}
			else {
				for(int j=1;j<=4;j++) {
				System.out.print(ch++);
				}
				System.out.println();
				}
		}
				
			
		}

	}


